{
    "projectUid": "{{projectUid}}",
    //HTML Inscription as an example but could be any
    "contentType":"{{HTMLInscriptionContentType}}",
    "b64File":"{{HTMLInscriptionB64Content}}",

    "metadata": {
        "name": "My single token {{Timestamp}}",
         "subType": "single",
         "subTypeData": "{\"description\": \"this is description\" }",
        "type": "ord",
        "description": "this is a standalone token description",
        "info": "anything",
        "eyes": "green"
    }
}

{
    "projectUid": "{{projectUid}}",
    //Image Inscription for a collection
    "contentType":"{{ImageInscriptionContentType}}",
    "b64File":"{{ImageInscriptionB64Content}}",
    "metadata": {
        "name": "My single token {{Timestamp}}",
         "subType": "collection",
         "subTypeData": "{\"description\": \"this is description\" }",
        "type": "ord",
        "description": "this is a collection token description",
        "info": "anything",
        "eyes": "green"
    }
}

{
    "projectUid": "{{projectUid}}",
    //Image Inscription for a collectionItem is the normnal one but others can also be used
    "contentType":"{{TextInscriptionContentType}}",
    "b64File":"{{TextInscriptionB64Content}}",
    "metadata": {
        "name": "My single token {{Timestamp}}",
         "subType": "collectionItem",
         "subTypeData": "{\"description\": \"this is description\",\"collectionId\": \"{{txId}}_0\" }",
        "type": "ord",
        "description": "this is a collection token description",
        "info": "anything",
        "eyes": "green"
    }
}

{
    "transactionUid": "{{transactionUid}}",
    //Manual step, take txObj from the previous step, sign the transaction and paste the result here
    "tx": "SIGNED_TX"
}
